<?php
/**
 * File: admin_bank_soal.php
  * Halaman untuk Admin mengelola semua soal dari semua guru (Bank Soal Terpusat).
   */

   require_once 'config/koneksi.php';

   // Keamanan: Pastikan hanya ADMIN yang bisa akses
   if (!isset($_SESSION['user_id']) || $_SESSION['peran'] != 'admin') {
       header("Location: logout.php");
           exit();
           }

           $pesan = '';
           if (isset($_GET['status'])) {
               if ($_GET['status'] == 'sukses_hapus') {
                       $pesan = "<div class='alert alert-success'>Soal berhasil dihapus secara permanen.</div>";
                           } elseif ($_GET['status'] == 'sukses_edit') {
                                   $pesan = "<div class='alert alert-success'>Soal berhasil diperbarui.</div>";
                                       } elseif ($_GET['status'] == 'gagal') {
                                               $pesan = "<div class='alert alert-danger'>Terjadi kesalahan. Aksi tidak dapat diselesaikan.</div>";
                                                   }
                                                   }

                                                   // === LOGIKA FILTER DAN PENCARIAN ===
                                                   $filter_guru = $_GET['filter_guru'] ?? '';
                                                   $filter_mapel = $_GET['filter_mapel'] ?? '';

                                                   $sql_soal = "
                                                       SELECT bs.id, bs.mapel, bs.tipe_soal, bs.soal, bs.opsi_a, bs.opsi_b, bs.opsi_c, bs.opsi_d, bs.opsi_e, bs.kunci_jawaban, bs.skor, u.nama_lengkap AS nama_guru
                                                           FROM bank_soal bs
                                                               JOIN pengguna u ON bs.id_guru = u.id
                                                                   WHERE 1=1
                                                                   ";

                                                                   $params = [];
                                                                   $types = "";

                                                                   if (!empty($filter_guru)) {
                                                                       $sql_soal .= " AND u.id = ?";
                                                                           $params[] = &$filter_guru;
                                                                               $types .= "i";
                                                                               }
                                                                               if (!empty($filter_mapel)) {
                                                                                   $sql_soal .= " AND bs.mapel LIKE ?";
                                                                                       $mapel_param = "%" . $filter_mapel . "%";
                                                                                           $params[] = &$mapel_param;
                                                                                               $types .= "s";
                                                                                               }

                                                                                               $sql_soal .= " ORDER BY u.nama_lengkap, bs.mapel, bs.id DESC";

                                                                                               $stmt_soal = $koneksi->prepare($sql_soal);
                                                                                               if (!empty($params)) {
                                                                                                   $stmt_soal->bind_param($types, ...$params);
                                                                                                   }
                                                                                                   $stmt_soal->execute();
                                                                                                   $result_soal = $stmt_soal->get_result();


                                                                                                   // Ambil daftar guru untuk dropdown filter
                                                                                                   $result_guru_list = $koneksi->query("SELECT id, nama_lengkap FROM pengguna WHERE peran = 'guru' ORDER BY nama_lengkap ASC");

                                                                                                   include_once 'templates/header_admin.php';
                                                                                                   ?>

                                                                                                   <div class="d-flex justify-content-between align-items-center mb-4">
                                                                                                       <h1><i class="bi bi-journal-bookmark-fill me-2"></i>Bank Soal Terpusat</h1>
                                                                                                       </div>

                                                                                                       <?php if (!empty($pesan)) echo $pesan; ?>

                                                                                                       <div class="card shadow-sm mb-4">
                                                                                                           <div class="card-body">
                                                                                                                   <form action="admin_bank_soal.php" method="GET" class="row g-3 align-items-center">
                                                                                                                               <div class="col-md-5">
                                                                                                                                               <label for="filter_mapel" class="form-label">Filter Mata Pelajaran</label>
                                                                                                                                                               <input type="text" class="form-control" name="filter_mapel" id="filter_mapel" placeholder="e.g., Matematika" value="<?php echo htmlspecialchars($filter_mapel); ?>">
                                                                                                                                                                           </div>
                                                                                                                                                                                       <div class="col-md-5">
                                                                                                                                                                                                       <label for="filter_guru" class="form-label">Filter Guru Pembuat</label>
                                                                                                                                                                                                                       <select name="filter_guru" id="filter_guru" class="form-select">
                                                                                                                                                                                                                                           <option value="">Semua Guru</option>
                                                                                                                                                                                                                                                               <?php while($guru = $result_guru_list->fetch_assoc()): ?>
                                                                                                                                                                                                                                                                                       <option value="<?php echo $guru['id']; ?>" <?php if($filter_guru == $guru['id']) echo 'selected'; ?>>
                                                                                                                                                                                                                                                                                                                   <?php echo htmlspecialchars($guru['nama_lengkap']); ?>
                                                                                                                                                                                                                                                                                                                                           </option>
                                                                                                                                                                                                                                                                                                                                                               <?php endwhile; ?>
                                                                                                                                                                                                                                                                                                                                                                               </select>
                                                                                                                                                                                                                                                                                                                                                                                           </div>
                                                                                                                                                                                                                                                                                                                                                                                                       <div class="col-md-2 d-flex align-items-end">
                                                                                                                                                                                                                                                                                                                                                                                                                       <button type="submit" class="btn btn-primary w-100">Filter</button>
                                                                                                                                                                                                                                                                                                                                                                                                                                   </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                           </form>
                                                                                                                                                                                                                                                                                                                                                                                                                                               </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                               </div>


                                                                                                                                                                                                                                                                                                                                                                                                                                               <div class="card shadow-sm">
                                                                                                                                                                                                                                                                                                                                                                                                                                                   <div class="card-header">
                                                                                                                                                                                                                                                                                                                                                                                                                                                           <h5>Daftar Semua Soal</h5>
                                                                                                                                                                                                                                                                                                                                                                                                                                                               </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <div class="card-body">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <div class="table-responsive">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       <table class="table table-hover align-middle">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       <thead class="table-light">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <tr>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <th>Guru Pembuat</th>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <th>Mapel</th>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <th>Tipe</th>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <th style="width: 40%;">Pertanyaan</th>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <th class="text-center">Skor</th>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <th class="text-center">Aksi</th>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               </tr>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               </thead>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               <tbody>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <?php if ($result_soal->num_rows > 0): ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <?php while($soal = $result_soal->fetch_assoc()): ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       <?php $data_soal_json = htmlspecialchars(json_encode($soal), ENT_QUOTES, 'UTF-8'); ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <tr>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <td><?php echo htmlspecialchars($soal['nama_guru']); ?></td>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <td><?php echo htmlspecialchars($soal['mapel']); ?></td>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <td><span class='badge bg-secondary'><?php echo str_replace('_', ' ', $soal['tipe_soal']); ?></span></td>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <td><?php echo htmlspecialchars(strip_tags(substr($soal['soal'], 0, 120))); ?>...</td>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <td class="text-center"><span class="badge bg-success"><?php echo ($soal['tipe_soal'] == 'pilihan_ganda') ? $soal['skor'] : '-'; ?></span></td>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <td class="text-center">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       <div class='btn-group btn-group-sm'>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               <button class='btn btn-info' data-bs-toggle='modal' data-bs-target='#viewSoalModal' data-soal='<?php echo $data_soal_json; ?>'>View</button>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       <a href='admin_edit_soal.php?id=<?php echo $soal['id']; ?>' class='btn btn-warning'>Edit</a>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               <a href='admin_hapus_soal.php?id=<?php echo $soal['id']; ?>' class='btn btn-danger' onclick='return confirm("PERINGATAN: Anda akan menghapus soal ini secara permanen dari bank soal. Lanjutkan?")'>Delete</a>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   </td>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               </tr>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       <?php endwhile; ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <?php else: ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <tr>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               <td colspan="6" class="text-center text-muted p-4">Tidak ada soal yang ditemukan dengan filter yang dipilih.</td>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       </tr>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <?php endif; ?>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           </tbody>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       </table>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   </div>

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <div class="modal fade" id="viewSoalModal" tabindex="-1">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       <div class="modal-dialog modal-xl">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               <div class="modal-content">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <div class="modal-header">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <h5 class="modal-title">Detail Soal</h5>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <div class="modal-body">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <p class="mb-1"><strong>Guru:</strong> <span id="viewGuru"></span></p>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <p><strong>Mata Pelajaran:</strong> <span id="viewMapel"></span></p>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <hr>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <div id="viewSoalContent"></div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <div class="modal-footer">
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Tutup</button>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   </div>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   </div>

                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   <script>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   document.addEventListener('DOMContentLoaded', function() {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       const viewModal = document.getElementById('viewSoalModal');
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           if (viewModal) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   viewModal.addEventListener('show.bs.modal', function(event) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               const data = JSON.parse(event.relatedTarget.getAttribute('data-soal'));
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       viewModal.querySelector('#viewGuru').textContent = data.nama_guru;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   viewModal.querySelector('#viewMapel').textContent = data.mapel;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           let content = `<div>${data.soal}</div>`;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       if (data.tipe_soal === 'pilihan_ganda') {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       content += '<hr><h6>Opsi Jawaban:</h6><ul class="list-group">';
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       ['a', 'b', 'c', 'd', 'e'].forEach(opt => {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           if (data['opsi_' + opt]) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   let itemClass = (data.kunci_jawaban.toUpperCase() === opt.toUpperCase()) ? 'list-group-item-success' : '';
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           content += `<li class="list-group-item ${itemClass}"><b>${opt.toUpperCase()}:</b> ${data['opsi_' + opt]}</li>`;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               });
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               content += '</ul>';
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               content += `<div class="mt-3"><strong>Skor:</strong> <span class="badge bg-success">${data.skor}</span></div>`;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                           }
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                       viewModal.querySelector('#viewSoalContent').innerHTML = content;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               });
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                   }
