    */

    require_once 'config/koneksi.php'; // <-- INI ADALAH BAGIAN YANG DIPERBAIKI

    // Keamanan: Pastikan hanya ADMIN yang bisa akses
    if (!isset($_SESSION['user_id']) || $_SESSION['peran'] != 'admin') {
        header("Location: logout.php");
            exit();
            }

            // === LOGIKA UNTUK MENGAMBIL DATA STATISTIK RINGKAS ===
            $total_guru = $koneksi->query("SELECT COUNT(id) as total FROM pengguna WHERE peran = 'guru'")->fetch_assoc()['total'];
            $total_siswa = $koneksi->query("SELECT COUNT(id) as total FROM pengguna WHERE peran = 'siswa'")->fetch_assoc()['total'];
            $total_kelas = $koneksi->query("SELECT COUNT(id) as total FROM kelas")->fetch_assoc()['total'];

            // === LOGIKA UNTUK MENGAMBIL LAPORAN AKTIVITAS PENGGUNA ===
            // Guru Paling Aktif (berdasarkan jumlah kelas)
            $result_guru_aktif = $koneksi->query("
                SELECT u.nama_lengkap, COUNT(k.id) AS jumlah_kelas
                    FROM pengguna u
                        LEFT JOIN kelas k ON u.id = k.id_guru
                            WHERE u.peran = 'guru'
                                GROUP BY u.id
                                    ORDER BY jumlah_kelas DESC
                                        LIMIT 10
                                        ");

                                        // Siswa Paling Aktif (berdasarkan jumlah kelas yang diikuti)
                                        $result_siswa_aktif = $koneksi->query("
                                            SELECT u.nama_lengkap, COUNT(pk.id) AS jumlah_kelas
                                                FROM pengguna u
                                                    LEFT JOIN pendaftaran_kelas pk ON u.id = pk.id_siswa
                                                        WHERE u.peran = 'siswa'
                                                            GROUP BY u.id
                                                                ORDER BY jumlah_kelas DESC
                                                                    LIMIT 10
                                                                    ");

                                                                    // Kelas Terpopuler (berdasarkan jumlah siswa)
                                                                    $result_kelas_populer = $koneksi->query("
                                                                        SELECT k.nama_kelas, k.mata_pelajaran, u.nama_lengkap AS nama_guru, COUNT(pk.id_siswa) AS jumlah_siswa
                                                                            FROM kelas k
                                                                                JOIN pengguna u ON k.id_guru = u.id
                                                                                    LEFT JOIN pendaftaran_kelas pk ON k.id = pk.id_kelas
                                                                                        GROUP BY k.id, k.nama_kelas, k.mata_pelajaran, u.nama_lengkap
                                                                                            ORDER BY jumlah_siswa DESC
                                                                                                LIMIT 10
                                                                                                ");

                                                                                                // Analisis Kuis (berdasarkan nilai rata-rata)
                                                                                                $result_analisis_kuis = $koneksi->query("
                                                                                                    SELECT s.judul_soal, k.nama_kelas, u.nama_lengkap AS nama_guru, 
                                                                                                               COUNT(pn.id) AS jumlah_pengerjaan, 
                                                                                                                          AVG(pn.nilai) AS rata_rata_nilai
                                                                                                                              FROM soal s
                                                                                                                                  JOIN kelas k ON s.id_kelas = k.id
                                                                                                                                      JOIN pengguna u ON k.id_guru = u.id
                                                                                                                                          LEFT JOIN penilaian pn ON s.id = pn.id_soal
                                                                                                                                              GROUP BY s.id, s.judul_soal, k.nama_kelas, u.nama_lengkap
                                                                                                                                                  ORDER BY rata_rata_nilai DESC
                                                                                                                                                      LIMIT 10
                                                                                                                                                      ");

                                                                                                                                                      // Performa Guru (berdasarkan rata-rata nilai siswanya)
                                                                                                                                                      $result_performa_guru = $koneksi->query("
                                                                                                                                                          SELECT 
                                                                                                                                                                  u.nama_lengkap, 
                                                                                                                                                                          COUNT(DISTINCT k.id) AS jumlah_kelas,
                                                                                                                                                                                  (SELECT AVG(pn.nilai) FROM penilaian pn JOIN soal s ON pn.id_soal = s.id JOIN kelas kl ON s.id_kelas = kl.id WHERE kl.id_guru = u.id) AS rata_rata_keseluruhan
                                                                                                                                                                                      FROM pengguna u
                                                                                                                                                                                          LEFT JOIN kelas k ON u.id = k.id_guru
                                                                                                                                                                                              WHERE u.peran = 'guru'
                                                                                                                                                                                                  GROUP BY u.id, u.nama_lengkap
                                                                                                                                                                                                      ORDER BY rata_rata_keseluruhan DESC
                                                                                                                                                                                                          LIMIT 10
                                                                                                                                                                                                          ");

                                                                                                                                                                                                          // (BARU) Logika untuk Performa Siswa dengan Filter
                                                                                                                                                                                                          $filter_kelas_siswa = $_GET['filter_kelas_siswa'] ?? 'semua';
                                                                                                                                                                                                          $filter_mapel_siswa = $_GET['filter_mapel_siswa'] ?? '';

                                                                                                                                                                                                          // Query ini akan mengambil semua siswa terlebih dahulu, lalu menghitung statistik mereka berdasarkan filter yang ada.
                                                                                                                                                                                                          // Ini lebih akurat karena memisahkan data pendaftaran dari data penilaian.
                                                                                                                                                                                                          $sql_performa_siswa = "
                                                                                                                                                                                                              SELECT
                                                                                                                                                                                                                      u.nama_lengkap,
                                                                                                                                                                                                                              (SELECT COUNT(*) FROM pendaftaran_kelas pk WHERE pk.id_siswa = u.id) as jumlah_kelas_diikuti,
                                                                                                                                                                                                                                      COUNT(pn.id) AS jumlah_kuis_dikerjakan,
                                                                                                                                                                                                                                              AVG(pn.nilai) AS rata_rata_nilai
                                                                                                                                                                                                                                                  FROM
                                                                                                                                                                                                                                                          pengguna u
                                                                                                                                                                                                                                                              LEFT JOIN
                                                                                                                                                                                                                                                                      penilaian pn ON u.id = pn.id_siswa
                                                                                                                                                                                                                                                                          LEFT JOIN
                                                                                                                                                                                                                                                                                  soal s ON pn.id_soal = s.id
                                                                                                                                                                                                                                                                                      LEFT JOIN
                                                                                                                                                                                                                                                                                              kelas k ON s.id_kelas = k.id
                                                                                                                                                                                                                                                                                                  WHERE
                                                                                                                                                                                                                                                                                                          u.peran = 'siswa'
                                                                                                                                                                                                                                                                                                          ";

                                                                                                                                                                                                                                                                                                          $params_siswa = [];
                                                                                                                                                                                                                                                                                                          $types_siswa = "";

                                                                                                                                                                                                                                                                                                          if ($filter_kelas_siswa !== 'semua' && is_numeric($filter_kelas_siswa)) {
                                                                                                                                                                                                                                                                                                              // Filter siswa yang terdaftar di kelas spesifik
                                                                                                                                                                                                                                                                                                                  $sql_performa_siswa .= " AND u.id IN (SELECT id_siswa FROM pendaftaran_kelas WHERE id_kelas = ?)";
                                                                                                                                                                                                                                                                                                                      $params_siswa[] = intval($filter_kelas_siswa);
                                                                                                                                                                                                                                                                                                                          $types_siswa .= 'i';
                                                                                                                                                                                                                                                                                                                          }
                                                                                                                                                                                                                                                                                                                          if (!empty($filter_mapel_siswa)) {
                                                                                                                                                                                                                                                                                                                              // Filter siswa yang terdaftar di kelas dengan mapel yang cocok
                                                                                                                                                                                                                                                                                                                                  $sql_performa_siswa .= " AND u.id IN (SELECT pk.id_siswa FROM pendaftaran_kelas pk JOIN kelas kl ON pk.id_kelas = kl.id WHERE kl.mata_pelajaran LIKE ?)";
                                                                                                                                                                                                                                                                                                                                      $mapel_param_siswa = "%" . $filter_mapel_siswa . "%";
                                                                                                                                                                                                                                                                                                                                          $params_siswa[] = $mapel_param_siswa;
                                                                                                                                                                                                                                                                                                                                              $types_siswa .= 's';
                                                                                                                                                                                                                                                                                                                                              }

                                                                                                                                                                                                                                                                                                                                              $sql_performa_siswa .= " GROUP BY u.id, u.nama_lengkap HAVING jumlah_kuis_dikerjakan > 0 ORDER BY rata_rata_nilai DESC";

                                                                                                                                                                                                                                                                                                                                              $stmt_performa_siswa = $koneksi->prepare($sql_performa_siswa);
                                                                                                                                                                                                                                                                                                                                              if (!empty($params_siswa)) {
                                                                                                                                                                                                                                                                                                                                                  // Penggunaan call_user_func_array untuk bind_param dinamis
                                                                                                                                                                                                                                                                                                                                                      call_user_func_array(array($stmt_performa_siswa, 'bind_param'), array_merge(array($types_siswa), $params_siswa));
                                                                                                                                                                                                                                                                                                                                                      }
                                                                                                                                                                                                                                                                                                                                                      $stmt_performa_siswa->execute();
                                                                                                                                                                                                                                                                                                                                                      $result_performa_siswa = $stmt_performa_siswa->get_result();
                                                                                                                                                                                                                                                                                                                                                      // Ambil daftar kelas untuk filter
                                                                                                                                                                                                                                                                                                                                                      $result_kelas_list = $koneksi->query("SELECT id, nama_kelas, mata_pelajaran FROM kelas ORDER BY nama_kelas ASC");


