<?php
require_once 'config/koneksi.php';

// Proteksi halaman, hanya untuk guru
if (!isset($_SESSION['user_id']) || $_SESSION['peran'] != 'guru') {
    header("Location: login.php");
        exit();
        }
        $id_guru_login = $_SESSION['user_id'];

        if (!isset($_GET['id'])) {
            header("Location: kolaborasi_index.php");
                exit();
                }
                $id_post = intval($_GET['id']);
                include_once 'templates/header.php';

                // Ambil data post
                $stmt_post = $koneksi->prepare("
                    SELECT p.id, p.id_guru, p.judul, p.isi_post, p.waktu_post, u.nama_lengkap AS nama_guru
                        FROM kolaborasi_post p
                            JOIN pengguna u ON p.id_guru = u.id
                                WHERE p.id = ?
                                ");
                                $stmt_post->bind_param("i", $id_post);
                                $stmt_post->execute();
                                $result_post = $stmt_post->get_result();
                                if ($result_post->num_rows == 0) {
                                    echo "<div class='alert alert-danger'>Postingan tidak ditemukan.</div>";
                                        include_once 'templates/footer.php';
                                            exit();
                                            }
                                            $post = $result_post->fetch_assoc();
                                            $stmt_post->close();

                                            // Ambil data komentar
                                            $stmt_komentar = $koneksi->prepare("
                                                SELECT k.id, k.id_guru, k.isi_komentar, k.waktu_komentar, u.nama_lengkap AS nama_guru
                                                    FROM kolaborasi_komentar k
                                                        JOIN pengguna u ON k.id_guru = u.id
                                                            WHERE k.id_post = ?
                                                                ORDER BY k.waktu_komentar ASC
                                                                ");
                                                                $stmt_komentar->bind_param("i", $id_post);
                                                                $stmt_komentar->execute();
                                                                $result_komentar = $stmt_komentar->get_result();
