                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          <h3 class="text-center mb-3">Papan Peringkat Kelas: <?php echo htmlspecialchars($info_kelas_terpilih['nama_kelas']); ?></h3>
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                  
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          <?php if ($peran_pengguna == 'siswa' && $peringkat_saya !== null): ?>
