<?php
require_once 'config/koneksi.php';

// Proteksi halaman
if (!isset($_SESSION['user_id'])) {
    header("Location: login.php");
        exit();
        }
        $id_pengguna = $_SESSION['user_id'];
        $peran_pengguna = $_SESSION['peran'];

        // Tentukan header yang akan digunakan
        $header_file = 'templates/header.php'; // Default untuk Guru
        if ($peran_pengguna == 'admin') {
            $header_file = 'templates/header_admin.php';
            } elseif ($peran_pengguna == 'siswa') {
                $header_file = 'templates/header_siswa.php';
                }
                include_once $header_file;

                // Ambil pesan masuk (Inbox)
                $stmt_inbox = $koneksi->prepare("
                    SELECT p.id, p.subjek, p.waktu_kirim, p.status_baca, pengirim.nama_lengkap AS nama_pengirim
                        FROM pesan p
                            JOIN pengguna pengirim ON p.id_pengirim = pengirim.id
                                WHERE p.id_penerima = ?
                                    ORDER BY p.waktu_kirim DESC
                                    ");
                                    $stmt_inbox->bind_param("i", $id_pengguna);
                                    $stmt_inbox->execute();
                                    $result_inbox = $stmt_inbox->get_result();

                                    // Ambil pesan terkirim (Sent)
                                    $stmt_sent = $koneksi->prepare("
                                        SELECT p.id, p.subjek, p.waktu_kirim, p.status_baca, penerima.nama_lengkap AS nama_penerima
                                            FROM pesan p
                                                JOIN pengguna penerima ON p.id_penerima = penerima.id
                                                    WHERE p.id_pengirim = ?
                                                        ORDER BY p.waktu_kirim DESC
                                                        ");
                                                        $stmt_sent->bind_param("i", $id_pengguna);
                                                        $stmt_sent->execute();
